import React , {useState} from "react"
import {addToList} from ""

const AddSong = () =>{
    const [song , setSong] = useState('intial song');


    const addToLocalState = (e) =>{
        setSong(e.target.value)
        
    }

    const sendToList = () =>{
        //call a function
        addToList(song);

    }


    return(
        <div>
            <form onSubmit={sendToList}>
                <input type="text" onChange={addToLocalState}/>
                <button> Submit</button>

            </form>

        </div>
    )


}

export default AddSong;
