import React,{Component} from "react";




class PieChart1 extends Component{
    state={
        labels : ["Accepted","Wrong Answer","Time Limit Exceeded","Compilation Error",
        "Runtime Error","Memory Limit Exceeded"],
        datasets : [{
            data : [],
            backgroundColor : ["green","red","brown","black","orange","blue"]
        }]
    }

    set(){
        console.log(this.props)
        let count_AC=0,count_WA=0,count_TLE=0,count_CE=0,count_RTE=0,count_MLE=0;
        for(let i=0;i<this.props.res.length;i++){
            if(this.props.res[i].verdict==="OK"){
                count_AC+=1;
            }
            else if(this.props.res[i].verdict==="WRONG_ANSWER"){
                count_WA+=1;
            }
            else if(this.props.res[i].verdict==="TIME_LIMIT_EXCEEDED"){
                count_TLE+=1;
            }
            else if(this.props.res[i].verdict==="COMPILATION_ERROR"){
                count_CE+=1;
            }
            else if(this.props.res[i].verdict==="RUNTIME_ERROR"){
                count_RTE+=1;
            }
            else if(this.props.res[i].verdict==="MEMORY_LIMIT_EXCEEDED"){

                count_MLE+=1;
            }
        }


        this.setState({datasets : [count_AC,count_WA,count_TLE,count_CE,count_RTE,count_MLE]})
        console.log(this.state);

    }
    
    
    render(){
        return(
            <div>
                hi
                <button onClick ={this.set}>Click me</button>
            </div>
        )

    }
    
}





export default PieChart1;
