import React, {useState} from "react";
import AddSong from "./AddSong";

const SongList= () =>{
    //state={ data =[  {name : "abcd" , id : 1 } , {name : "defg" , id : 2}    ] }

    const [data ,setData ] = useState([
        {name : "abcd" , id : 1 } ,
        {name : "defg" , id : 2}
    ])

    const list = data.map(x=>{
        return(
            <li key={x.id}>{x.name}</li>
        )
    })

    const change = () =>{
        setData([...data, {name : "your self" , id : 13}])
    }

    return(
        <div className="song-list">
            <ul>
                {list}
            </ul>

            <button onClick = {change}> Add me </button>
            <AddSong/>

        

        </div>
    )

}

export default SongList
